export const hasUserEnabledAudioPermissions = (): boolean => {
  const audioContext = new window.AudioContext();
  console.log(audioContext.state);
  return audioContext.state !== "suspended";
};

export const promptUserToEnableAudioPermissions = async (): Promise<void> => {
  const silentAudio = new Audio();
  silentAudio.src =
    "data:audio/mpeg;base64,";
  silentAudio.volume = 0.01;
  try {
    await silentAudio.play();
  } catch {
    // noop - trigger this "failure" so that the browser prompts the user to enable audio
  }
};

export const playSound = (file: string): void => {
  const audio = new Audio(new URL(file, window.location.href).toString());
  audio.play();
};
